create table company ( c_id int primary key, c_name varchar2(50) );
create table payroll ( trans_id int primary key, gross_pay int, net_pay int, deduc- tion int, ta int, ma int );
create table manager( mg_id int primary key, mg_f_name varchar2(20) not null, mg_l_name varchar2(20) not null );
create table department( dep_id int primary key, dep_name varchar2(20) not null, c_id int references company, mg_id int references manager );
create table employee ( e_id int primary key, emp_f_name varchar2(50) not null, emp_l_name varchar2(50) not null, dob varchar2(50) not null, sex char (1), mg_id int references manager, trans_id int references payroll );
create table employee_location(pincode int primary key, street varchar2(50) not null, h_no varchar2(50) not null, city varchar2(50) not null, e_id int references employee);
create table employee_phone_no ( phone_no number(10), emp_id int references employee );
